import React, { useState } from "react";
import "./delivery.css";

export default function DeliveryForm({ onClose, cartItems, onOrderAccepted }) {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [nameError, setNameError] = useState(false);
  const [phoneError, setPhoneError] = useState(false);

  const orderPrice = cartItems ? cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0) : 0;
  const deliveryPrice = 5000;
  const totalPrice = orderPrice + deliveryPrice;

  const handleSubmit = () => {
    let hasError = false;
    if (name.trim() === "") {
      setNameError(true);
      hasError = true;
    } else {
      setNameError(false);
    }
    const phoneRegex = /^\d{9}$/;
    if (!phoneRegex.test(phone)) {
      setPhoneError(true);
      hasError = true;
    } else {
      setPhoneError(false);
    }
    if (hasError) {
      alert("Iltimos, barcha maydonlarni to'g'ri to'ldiring!");
      return;
    }
    onOrderAccepted();
  };
  return (
    <div className="delivery-modal">
        {/* Header */}
        <div className="delivery-header">
          <h1 className="delivery-title">Yetkazib berish</h1>

          {/* Close Button */}
          <button className="cart-close" onClick={onClose}>✕</button>
            {/* <span aria-hidden="true">&times;</span> */}
        </div>

        {/* Personal Information */}
        <div className="form-group">
          <label>Shaxsiy ma'lumotlar</label>
          <input type="text" placeholder="Ismingizni kiriting" value={name} onChange={(e) => setName(e.target.value)} className={nameError ? "error" : ""} required />
        </div>

        {/* Phone */}
        <div className="form-group">
          <label>Telefon raqamingiz</label>
          <input type="tel" placeholder="901234567" value={"+998" + phone} onChange={(e) => {
            const val = e.target.value;
            if (val.startsWith("+998")) {
              setPhone(val.slice(5).replace(/\D/g, ''));
            } else {
              setPhone(val.replace(/\D/g, ''));
            }
          }} className={phoneError ? "error" : ""} required />
        </div>

        {/* Order Box */}
        <div className="order-box">
          <h2 className="order-title">Buyurtmadagi mahsulotlar</h2>

          <div className="order-list">
            {cartItems && cartItems.filter(item => item.quantity > 0).map(item => (
              <div key={item.id} className="order-item">
                <span>{item.name} × {item.quantity}</span>
                <span>{(item.price * item.quantity).toLocaleString()} UZS</span>
              </div>
            ))}

            <div className="divider"></div>

            <div className="order-item">
              <span>Mahsulotlar jami:</span>
              <span>{orderPrice.toLocaleString()} UZS</span>
            </div>

            <div className="order-item">
              <span>Yetkazib berish:</span>
              <span>{deliveryPrice.toLocaleString()} UZS</span>
            </div>

            <div className="divider"></div>
          </div>
        </div>

        {/* Total */}
        <div className="total">
          <span>Jami to'lov</span>
          <span>{totalPrice.toLocaleString()} UZS</span>
        </div>

        {/* Button */}
        <button className="confirm-btn" onClick={handleSubmit}>Rasmiylashtirish</button>
      </div>
  );
}
