import React, { useState, useEffect } from 'react'
import "./order-accepted.css"
import { useNavigate } from 'react-router-dom'
const OrderAccepted = ({ onClose }) => {
  const navigate = useNavigate();
  const [timeLeft, setTimeLeft] = useState(40 * 60); // 40 minutes in seconds

  useEffect(() => {
    // Load last order time
    const orderHistory = JSON.parse(localStorage.getItem('orderHistory') || '[]');
    if (orderHistory.length > 0) {
      const lastOrder = orderHistory[orderHistory.length - 1];
      const orderTime = new Date(lastOrder.date).getTime();
      const currentTime = new Date().getTime();
      const elapsedSeconds = Math.floor((currentTime - orderTime) / 1000);
      const remainingSeconds = Math.max(0, 40 * 60 - elapsedSeconds);
      setTimeLeft(remainingSeconds);
    }

    // Timer countdown
    const timer = setInterval(() => {
      setTimeLeft(prev => {
        if (prev <= 1) {
          clearInterval(timer);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);
  return (
    <div className="order-accepted-wrapper">
      <div className='order-accepted'>
        <button onClick={onClose} className="exit-button">
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <line x1="18" y1="6" x2="6" y2="18"></line>
            <line x1="6" y1="6" x2="18" y2="18"></line>
          </svg>
        </button>
        <div className='order-accepted-circle'>
          <svg xmlns="http://www.w3.org/2000/svg" 
               className="check-icon"
               viewBox="0 0 24 24">
            <path d="M20 6 9 17l-5-5"/>
          </svg>
        </div>
        <h2 className="order-title">Buyurtma qabul qilindi</h2>
        <p className="order-text">Taomlaringizni tez orada yetkazib</p>
        <p className='edd'>beramiz</p>
        <div className="timer">
          <p>Qolgan vaqt: {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, '0')}</p>
        </div>
        <br />
        {/* <button className='ord-btn' onClick={() => { onClose(); navigate('/'); }}>Bosh sahifaga qaytish</button> */}
        <button
  className='ord-btn'
  onClick={() => {
    onClose();
    navigate('/');
    setTimeout(() => {
      window.location.reload(); // ðŸ”„ sahifani yangilash
    }, 100);
  }}
>
  Bosh sahifaga qaytish
</button>
      </div>
    </div>
  )
}

export default OrderAccepted
