export default function Menu({ selectedCategory, addToCart }) {
  const [showCard, setShowCard] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  // ðŸ‘‰ tartibni shu yerga joylashtirasiz
  const order = ["Go'shtli", "Tovoqli", "Kartoshka", "Sous"];

  const filteredProducts = products
    .filter((item) => item.category === selectedCategory)
    .sort((a, b) => {
      const aIndex = order.findIndex((name) =>
        a.name.toLowerCase().includes(name.toLowerCase())
      );
      const bIndex = order.findIndex((name) =>
        b.name.toLowerCase().includes(name.toLowerCase())
      );

      return aIndex - bIndex;
    });

  const handleProductClick = (product) => {
    setSelectedProduct(product);
    setShowCard(true);
  };

  const handleCloseCard = () => {
    setShowCard(false);
    setSelectedProduct(null);
  };

  return (
    <section className="products" id="menu">
      <div className="container">
        <div className="products-grid">
          {filteredProducts.map((product) => (
            <div key={product.id} onClick={() => handleProductClick(product)}>
              <ProductCard product={product} />
            </div>
          ))}
        </div>
      </div>

      {showCard && (
        <Card product={selectedProduct} onClose={handleCloseCard} addToCart={addToCart} />
      )}
    </section>
  );
}
